import React from 'react';
import axios from 'axios';

class Comments extends React.Component{
    constructor(props){
        super(props)

        this.state = {
            comments : []
        }
    }
    componentDidMount(){
        axios.get('https://jsonplaceholder.typicode.com/comments')
    }
}

export default Comments
